mapper.select:
select distinct m.* from sys_user_role ur, sys_role_resource rs, sys_menu m where ur.role_id= rs.role_id and rs.resource_id = m.menu_id and rs.resource_type = 1 and m.del_flag != -1
and ur.user_id = #{userId}
order by m.menu_order

select distinct m.* from sys_user_role ur, sys_role_resource rs, sys_menu m where ur.role_id = rs.role_id and rs.resource_id = m.menu_id and rs.resource_type = 2 and m.parent_id = #{menuId} and m.del_flag != -1
and ur.user_id = #{userId}
order by m.menu_order

select count(1)
from sys_menu m
where m.del_flag != -1

    and m.menu_type = #{menuType}


    and m.parent_id = #{parentId}


    and CONCAT(IFNULL(m.menu_name,''),IFNULL(m.menu_url,'')) like #{keyword}

select m.menu_id, m.menu_name, m.menu_url, m.parent_id, m.menu_order, m.menu_type, m.removable, m.description, m.del_flag from sys_menu m where m.del_flag != -1
and m.menu_type = #{menuType}
and m.parent_id = #{parentId}
and CONCAT(IFNULL(m.menu_name,''),IFNULL(m.menu_url,'')) like #{keyword}
limit #{from},#{size}

select m.menu_id, m.menu_name, m.menu_url, m.parent_id, m.menu_order, m.menu_type, m.removable, m.description, m.del_flag from sys_menu m where m.del_flag != -1
and m.menu_type = #{menuType}
and m.parent_id = #{parentId}

select * from sys_menu
where del_flag != -1
and menu_id = #{menuId}

select * from sys_menu
where del_flag != -1
and menu_url = #{url}
